pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

error EquityManager__TotalValueGreaterThan100();
error EquityManager__StakeIsZero();
error EquityManager__TransactionFailed();
error EquityManager__InsufficientBalance();
error EquityManager__ContractUpdateWindowIsOpen();

/// @custom:security-contact info@impact3.io
contract EquityManager is Pausable, AccessControl, ReentrancyGuard {
    using SafeMath for uint256;

    // FundsReceived event is triggered when funds are recieved to the contract
    event FundsReceived(address indexed addr, uint256 amount);

    // FundsDistributed is triggered when funds are distributed from the contract
    event FundsDistributed(
        uint256 amount_sent_to_stakeholders,
        uint256 time_distributed
    );

    // EquityStakeUpdated is triggered when an existing stakeholder is updated
    event EquityStakeUpdated(uint256 timeUpdated);

    // NewStakeHolderAdded is triggered when a new stakeholder is added to the contract
    event NewStakeHolderAdded(address holder, uint256 stake, uint256 timeAdded);

    // ExistingStakeHolderEquityIncrease is triggered when an existing stakeholder equity is increased
    event ExistingStakeHolderEquityIncrease(
        address holder,
        uint256 stake,
        uint256 timeAdded
    );

    // Roles
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    // Equity Stake struct encapsulates an equity stake for a specific stakeholder
    struct EquityStake {
        uint256 stakePercentage;
        uint256 fundsLastReceived;
        uint256 equityLastUpdated;
    }

    // TODO: lets change this to a mapping and remove Istakeholders

    address[] public current_stakeholders;

    // mapping
    mapping(address => EquityStake) public current_stakes;

    address payable public i3Fund;

    uint256 public immutable i_interval;
    uint256 public immutable i_window;
    uint256 private s_lastTimeStamp;

    constructor(
        address _i3Fund,
        uint256 _interval,
        uint256 _window
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
        setFund(_i3Fund);
        i_interval = _interval;
        i_window = _window;
        s_lastTimeStamp = block.timestamp;
    }

    /////////////////////
    // Main Functions //
    /////////////////////

    function setFund(address fund) public onlyRole(MANAGER_ROLE) {
        i3Fund = payable(fund);
    }

    /*
     * @notice Method for distributing funds
     * @notice equity stake is updated at the every stipulated interval
     */
    function distributeFunds() public whenNotPaused nonReentrant {
        if (!contractUpdateWindow()) {
            uint256 fundsToDistribute = address(this).balance;
            uint256 fundsDistributed = 0;

            address[] memory curentholders = current_stakeholders;
            for (uint256 i = 0; i < curentholders.length; i++) {
                uint256 amount = fundsToDistribute
                    .mul(current_stakes[curentholders[i]].stakePercentage)
                    .div(100 * (10**18));

                current_stakes[curentholders[i]].fundsLastReceived = block
                    .timestamp;
                fundsDistributed += amount;
                sendValue(payable(current_stakeholders[i]), amount);
            }

            sendValue(i3Fund, address(this).balance);
            emit FundsDistributed(fundsDistributed, block.timestamp);
        }
        revert EquityManager__ContractUpdateWindowIsOpen();
    }

    function totalStake(address[] memory _currentHolders)
        internal
        view
        returns (uint256)
    {
        uint256 _totalStake = 0;
        for (uint256 i = 0; i < _currentHolders.length; i++) {
            _totalStake += current_stakes[_currentHolders[i]].stakePercentage;
        }
        return _totalStake;
    }

    /*
     * @notice Method for adding new stakeholder
     * @notice Only the manager can call this funtion
     * @param holder, new stakeholder's address
     * @param stake, new stakeholder's stake in %
     */
    function addHolderAndUpdateStake(address _holder, uint256 _stake)
        public
        whenNotPaused
        onlyRole(MANAGER_ROLE)
    {
        if (!contractUpdateWindow()) {
            revert EquityManager__ContractUpdateWindowIsOpen();
        }

        if (_stake <= 0) {
            revert EquityManager__StakeIsZero();
        }
        if (totalStake(current_stakeholders).add(_stake) > (100 * (10**18))) {
            revert EquityManager__TotalValueGreaterThan100();
        }
        //Adding new stakeholder
        if (current_stakes[_holder].stakePercentage == 0) {
            current_stakeholders.push(_holder);

            current_stakes[_holder] = EquityStake(
                _stake,
                block.timestamp,
                block.timestamp
            );

            emit NewStakeHolderAdded(_holder, _stake, block.timestamp);
        }

        // Updating equities of existing stakeholders
        current_stakes[_holder].stakePercentage = current_stakes[_holder]
            .stakePercentage
            .add(_stake);
        current_stakes[_holder].equityLastUpdated = block.timestamp;

        emit ExistingStakeHolderEquityIncrease(
            _holder,
            _stake,
            block.timestamp
        );
    }

    function sendValue(address payable recipient, uint256 amount) internal {
        if (address(this).balance < amount) {
            revert EquityManager__InsufficientBalance();
        }
        (bool success, ) = recipient.call{value: amount}("");
        if (!success) {
            revert EquityManager__TransactionFailed();
        }
    }

    // TODO: Window
    // Danu: can we make this an interval of 7 days at the start of every month
    // that we can add and update  existing stake holders.
    // Also noone in this interval can distribute funds.

    function contractUpdateWindow() internal view returns (bool) {
        uint256 _opentimestamp = s_lastTimeStamp +
            (i_interval - (i_window / 2));
        uint256 _closetimestamp = s_lastTimeStamp +
            (i_interval + (i_window / 2));

        if (
            block.timestamp >= _opentimestamp &&
            block.timestamp < _closetimestamp
        ) {
            return true;
        }
        return false;
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function deposit() external payable {
        emit FundsReceived(msg.sender, msg.value);
    }

    /////////////////////
    // Getter Functions //
    /////////////////////

    /*
     * @notice Function for getting stakeholders and
     * their current equity stake
     */
    function getHoldersAndEquities()
        external
        view
        returns (address[] memory _holders, uint256[] memory _stakesPercent)
    {
        //EquityStake[] memory equitystake = current_stakes;
        _holders = new address[](current_stakeholders.length);
        _holders = current_stakeholders;
        _stakesPercent = new uint256[](_holders.length);
        for (uint256 i = 0; i < _holders.length; i++) {
            _stakesPercent[i] = current_stakes[_holders[i]].stakePercentage;
        }

        return (_holders, _stakesPercent);
    }

    function getContractBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function getImpact3Address() external view returns (address) {
        return i3Fund;
    }

    function PauserRole() external pure returns (bytes32) {
        return PAUSER_ROLE;
    }

    function getUpdateIntervalAndWindow()
        external
        view
        returns (uint256, uint256)
    {
        return (i_interval, i_window);
    }
}
